mapper.update:
UPDATE detail_holi
SET state = #{state}
WHERE holi_no = #{holiNo}

UPDATE detail_holi
SET state = '사용'
WHERE state = '승인' and startDate = #{strDate}

mapper.select:
select *
from detail_holi
where state = '사용' and startDate = #{strDate}

mapper.update:
UPDATE holi h
SET cntUsed = cntUsed + #{duration},
    remain = remain - #{duration}
WHERE emp_no = #{empNo}
